module.exports = function loader(content) {
  const packageSrc = JSON.parse(content); // eslint-disable-line no-unused-vars
  const final = {};
  for (const used of this.query.used) {
    let parts = used.split('.');
    let entry = final;
    for (const part of parts.slice(0, -1)) entry = entry[part] = {};
    entry[parts[parts.length - 1]] = eval(`packageSrc.${used}`);
  }
  return `module.exports = (${JSON.stringify(final)})`;
};
