/**
 * Promisifies a function
 * @param {Function} fn The function to promisify
 * @returns {Function} Promise based version of the original function
 */
declare function promisify(fn: (...args: any[]) => any): (...args: any[]) => Promise<any>;

/**
 * Promisifies an object with functions
 * @param {Object} obj The object to promisify
 * @param {string} [suffix='Async'] Suffix to append to the promisified function
 * @returns {Object} The updated object with promisified functions
 */
declare const promisifyAll: (obj: any, suffix?: string) => any;

export { promisify, promisifyAll, promisify as tsubakify, promisifyAll as tsubakifyAll };
